import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import classes from "../scss/ManufacturerBar.module.scss";

const ManufacturerBar = observer (() => {
    const {car} = useContext(Context)

    const elementClasses = [classes.ManufacturerBar__element]

    const handler = (manufacturer) => {
        elementClasses.push(classes.active)
        car.SetSelectedManufacturer(manufacturer)
        console.log(car.selectedManufacturer)
    }

    return (
        <div className={classes.ManufacturerBar}>
            {car.manufacturers.map(manufacturer =>
            <div className={elementClasses.join(' ')}
                 key={manufacturer.id}
                 onClick={() => handler(manufacturer)}
            >
                {manufacturer.id === car.selectedManufacturer.id }
                <span>{manufacturer.name}</span>
            </div>
            )}
        </div>
    );
})
export default ManufacturerBar;
