import React, {useContext} from 'react';
import classes from '../scss/Auth.module.scss'
import {REGISTRATION_ROUTE} from '../utils/consts'
import Layout from "../containers/Layout/Layout";
import {Context} from "../index";

const Auth = () => {
    const {user} = useContext(Context)
    return (
        <Layout>
            <div className={classes.Auth}>
                <h1>Авторизация</h1>
                <div className={classes['Auth__form']}>
                    <form action="">
                        <input placeholder={'+7 (908) 999-99-29'}/>
                        <input placeholder={'Введите пароль'}/>
                        {user.isAuth ?
                            <button>Войти</button>
                            :
                            <div className={classes['NavBar-nav__log']}>
                                <span>Admin</span>
                                <span onClick={() => user.setIsAuth(true)}>Вход и регистрация</span>
                            </div>
                        }
                    </form>
                </div>

                <span>Нет аккаунта?</span>
                <a href={REGISTRATION_ROUTE}>Регистрация</a>
            </div>
        </Layout>

    );
};

export default Auth;
